INSERT INTO `contents` VALUES ('00000000026', 'เงินอุดหนุนองค์กรสวัสดิการสังคม', 'เงินอุดหนุนองค์การสวัสดิการสังคม', 'เงินอุดหนุนองค์การสวัสดิการสังคม', 'เงินอุดหนุนองค์การสวัสดิการสังคม', '<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"620\">\n<tbody>\n<tr>\n<td class=\"font51\" align=\"center\">เงินอุดหนุนองค์การสวัสดิการสังคมภาคเอกชน</td>\n</tr>\n</tbody>\n</table>\n<table border=\"0\" cellspacing=\"2\" cellpadding=\"0\" width=\"620\">\n<tbody>\n<tr>\n<td class=\"font1\" align=\"left\"><span class=\"font21\">การจัดตั้ง</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&ldquo;เงินอุดหนุนองค์การสวัสดิการสังคมภาคเอกชน&rdquo;จัดตั้งตามระเบียบกระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์ ว่าด้วยการให้เงินอุดหนุนแก่องค์การสวัสดิการสังคมภาคเอกชน พ.ศ. ๒๕๔๖<br /><span class=\"font21\">วัตถุประสงค์</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;เพื่อส่งเสริมสนับสนุนให้องค์การสวัสดิการสังคมภาคเอกชน เข้ามามีส่วนร่วมในการจัดบริการสังคมสงเคราะห์ แก่ผู้ด้อยโอกาสทุกกลุ่มเป้าหมายรวมทั้งเป็นภารกิจต่อเนื่องในการช่วยเหลือ เลี้ยงดู และพัฒนาผู้ด้อยโอกาสที่จำต้องได้รับการช่วยเหลือ ๗ กลุ่มเป้าหมาย<br /><span class=\"font21\">กลุ่มเป้าหมาย</span>&nbsp;จำนวน ๗ กลุ่มเป้าหมาย คือ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- กลุ่มเด็กและเยาวชน<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- กลุ่มสตรี<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- กลุ่มคนพิการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- กลุ่มผู้สูงอายุ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- กลุ่มผู้ด้อยโอกาส<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- กลุ่มครอบครัวและชุมชน<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- กลุ่มส่งเสริมสนับสนุนเครือข่ายสวัสดิการสังคม<br /><span class=\"font21\">เงื่อนไขการสนับสนุน</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;องค์การสวัสดิการสังคมภาคเอกชนที่ส่งโครงการขอรับเงินอุดหนุนจากกระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์ จะต้องเป็นองค์การที่ขึ้นทะเบียนกับกระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์แล้ว โดยองค์การจะต้องมีคุณสมบัติ ดังนี้<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๑. เป็นองค์การนิติบุคคล สำหรับกลุ่มบุคคลให้ขอรับการสนับสนุนภายใต้องค์การนิติบุคคลและ มีหน่วยงานของรัฐให้การรับรองการดำเนินงาน (ซึ่งรวมถึงองค์การสวัสดิการสังคมภาคเอกชนที่ได้รับการขึ้นทะเบียนรับรองเป็นองค์กรสาธารณประโยชน์ ตามพระราชบัญญัติส่งเสริมการจัดสวัสดิการสังคม พ.ศ. 2546 ด้วย)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๒. เป็นองค์การที่มีประวัติผลงานและกิจกรรมเป็นที่น่าเชื่อถืออย่างน้อย ๑ ปี<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๓. เป็นองค์การที่มีวัตถุประสงค์และกิจกรรมเพื่อการพัฒนาสังคม การช่วยเหลือป้องกันและแก้ไขปัญหาทางสังคมที่เกิดขึ้นแก่ประชาชน ทั้งนี้โดยไม่หวังผลกำไร<br /><span class=\"font21\">การเสนอโครงการ</span>&nbsp;ให้จัดส่งเอกสารตามแบบที่กำหนด คือ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๑. คำขอขึ้นทะเบียนองค์การสวัสดิการสังคมภาคเอกชน และแนบสำเนาหลักฐานในส่วนที่เกี่ยวข้อง ดังนี้<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๑) สำเนาทะเบียนบ้านและสำเนาบัตรประจำตัวประชาชน (หรือสำเนาบัตรประจำตัว เจ้าหน้าที่ของรัฐ) ของผู้ขอ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๒) สำเนาตราสารและระเบียบข้อบังคับพร้อมใบจัดตั้ง<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๓) ผลงานในรอบปีที่ผ่านมา ๑ ปี<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๔) งบดุล และงบแสดงรายรับรายจ่ายในรอบปีที่ผ่านมา<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๕) หนังสือรับรองขององค์การนิติบุคคล หรือหนังสือรับรองของหน่วยงานของรัฐ (ในกรณีเป็นกลุ่มบุคคล)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๒. ข้อมูลเกี่ยวกับโครงการที่ขอรับเงินอุดหนุน<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๑) ข้อมูลทั่วไปเกี่ยวกับองค์การ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๒) ชื่อโครงการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๓) ความเป็นมา เหตุผลความจำเป็นในการดำเนินโครงการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๔) วัตถุประสงค์<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๕) เป้าหมาย กิจกรรมและตัวชี้วัดการประเมินโครงการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๖) งบประมาณ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๗) ผลที่คาดว่าจะได้รับ<br /><span class=\"font21\">กรอบวงเงินในการเสนอโครงการ</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๑. โครงการขนาดเล็ก หมายถึง โครงการที่ขอมาในวงเงินไม่เกิน ๕๐,๐๐๐ บาท<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๒. โครงการขนาดกลาง หมายถึง โครงการที่ขอมาในวงเงิน ตั้งแต่ ๕๐,๐๐๑ &ndash; ๓๐๐,๐๐๐ บาท<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๓. โครงการขนาดใหญ่ หมายถึง โครงการที่ขอมาในวงเงิน ตั้งแต่ ๓๐๐,๐๐๑ บาท ขึ้นไป&nbsp;<br /><span class=\"font21\">การพิจารณาโครงการ</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๑. ลักษณะโครงการที่สนับสนุน<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๑) โครงการที่ให้บริการสังคมสงเคราะห์ให้แก่ประชาชน และผู้ด้อยโอกาสทุกกลุ่มเป้าหมาย เน้นที่เป็นภารกิจต่อเนื่องในการช่วยเหลือ เลี้ยงดู และพัฒนาผู้ด้อยโอกาสที่จำต้องได้รับการช่วยเหลือ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๒) สอดคล้องกับยุทธศาสตร์ และขอบเขตงานของกระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๓) มีความชัดเจนในเรื่อง<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- วัตถุประสงค์ของโครงการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- ระบุจำนวนกลุ่มเป้าหมาย<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- งบประมาณ คุ้มค่า เหมาะสม<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๔) ในการเขียนโครงการให้กำหนดวิธีการประเมินผลด้วย<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๒. ลักษณะโครงการที่ไม่สนับสนุน&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๑) โครงการที่มีลักษณะซ้ำซ้อนในกลุ่มเป้าหมาย และพื้นที่รวมทั้งงานที่ภาครัฐดำเนินการครอบคลุมแล้ว<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๒) โครงการที่ขอรับการสนับสนุนเป็นค่าที่ดิน สิ่งก่อสร้าง หรือครุภัณฑ์และค่าใช้จ่ายสำนักงาน<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๓) โครงการที่มีการจ้างในลักษณะการจ่ายเป็นเงินเดือน<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๔) โครงการที่เน้นค่าอุปกรณ์ เครื่องมือ มากกว่ากิจกรรม<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๕) โครงการที่ใช้ทรัพยากรของรัฐ ดำเนินการในการปฏิบัติงานเป็นปกติของราชการเป็นส่วนใหญ่<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๓. องค์การสวัสดิการสังคมภาคเอกชน ต้องส่งรายงานผลการปฏิบัติงาน ตามข้อ ๙ ให้ครบก่อนตามที่กำหนด หากองค์กรใดที่เคยได้รับเงินแล้ว และไม่จัดส่งรายงานภายในระยะเวลาที่กำหนดจะไม่ได้รับการพิจารณาโครงการที่เสนอขอรับการสนับสนุนเงินอุดหนุนในปีถัดไป<br /><span class=\"font21\">จำนวนโครงการที่ขอรับการสนับสนุน&nbsp;</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;แต่ละองค์การในพื้นที่แต่ละจังหวัดจะส่งโครงการได้ไม่เกิน ๓ โครงการ<br /><span class=\"font21\">งบประมาณ&nbsp;</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;การพิจารณาจัดสรรงบประมาณแต่ละโครงการ จะพิจารณาตามหลักเกณฑ์โดยใช้อัตราการเบิกจ่ายเงิน ของกระทรวงการคลังเป็นเกณฑ์ จะพิจารณาให้การสนับสนุนในวงเงินไม่เกินร้อยละ ๗๕ ของค่าใช้จ่ายตามโครงการตามความจำเป็นและเหมาะสม และให้จัดทำแผนการใช้จ่ายเงินตามโครงการด้วย<br /><span class=\"font21\">แหล่งเงินทุน&nbsp;</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;เป็นเงินงบประมาณรายจ่ายประจำปีที่ได้รับการจัดสรรจากสำนักงบประมาณ<br /><span class=\"font21\">การจัดทำโครงการ<br /></span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"font21\">วิธีการเขียนโครงการ&nbsp;<br /></span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๑. ชื่อโครงการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;เขียนชื่อโครงการให้สอดคล้องกับวัตถุประสงค์และกิจกรรม โดยสื่อใจความให้กระชับ เข้าใจง่าย<br />และสอดคล้องกับวัตถุประสงค์ขององค์การ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๒. หลักการและเหตุผล<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;เป็นการระบุถึงสภาพปัญหาชี้แจงความเป็นมาและความจำเป็นในการจัดทำโครงการและปัญหาที่<br />ทำให้ต้องจัดทำโครงการขึ้นมา เพื่อแก้ไขปัญหาดังกล่าว<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๓. วัตถุประสงค์ของโครงการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;เป็นการแสดงให้เห็นจุดประสงค์ที่ต้องการจะให้เกิดขึ้นอย่างเห็นได้ชัดเจนจากการดำเนินงาน<br />ตามโครงการ โดยระบุว่าโครงการต้องการทำอะไร/มีกิจกรรมอะไรที่คิดจะทำ บอกให้ชัดเจนที่สุด<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๔. กลุ่มเป้าหมาย<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ระบุประเภทและจำนวนกลุ่มเป้าหมายของผู้รับบริการหรือผู้ได้รับประโยชน์จากการดำเนินงาน<br />ตามโครงการให้ชัดเจน<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๕. สถานที่ดำเนินการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ระบุสถานที่ดำเนินการโครงการ ระบุพื้นที่โดยระบุหมู่บ้าน ตำบล อำเภอ จังหวัด<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๖. ระยะเวลาดำเนินการโครงการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;เป็นการกำหนดช่วงเวลาการปฏิบัติโครงการ ตั้งแต่ระยะเริ่มต้นจนถึงการสิ้นสุดโครงการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๗. วิธีการดำเนินการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;แสดงถึงรายละเอียดกิจกรรมและกระบวนการปฏิบัติงานที่สอดคล้องกับวัตถุประสงค์มี<br />การกำหนดระยะเวลาของแต่ละกิจกรรมที่สมเหตุสมผล และควรมีกิจกรรมต่อเนื่อง หากเป็นโครงการที่มี การฝึกอบรมจะต้องมีกิจกรรมต่อเนื่องที่จะส่งผลต่อการคุ้มครอง การส่งเสริมและการสนับสนุน กลุ่มผู้ด้อยโอกาส และมีการกำหนดการฝึกอบรมหัวข้อที่จะฝึกอบรม<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๘. งบประมาณโครงการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ระบุค่าใช้จ่ายทั้งหมดของโครงการ ค่าใช้จ่ายที่ต้องการขอรับการสนับสนุน ควรแจกแจงรายการค่าใช้จ่ายในแต่ละรายการที่ขอให้ชัดเจนและสอดคล้องกับกิจกรรม โดยคำนึงถึงหลักประหยัดและสมเหตุสมผล และหากเป็นโครงการต่อเนื่องที่ได้รับการสนับสนุนเงินอุดหนุนในปีที่ผ่านมา ก็ให้ระบุค่าใช้จ่ายของโครงการและเงินที่ได้รับการสนับสนุนด้วย<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๙. การประเมินผลโครงการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ระบุตัวชี้วัดในการประเมินผลโครงการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๑๐. ผลที่คาดว่าจะได้รับ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ระบุผลที่คาดว่าจะได้รับจากการดำเนินงานตามโครงการที่สามารถวัดได้ในเชิงปริมาณ โดยเป็นผลที่เกิดขึ้นจากการที่โครงการบรรลุวัตถุประสงค์ ผลที่เกิดขึ้นจากการปฏิบัติโครงการ สามารถแสดงให้เห็นผลที่เป็นประโยชน์ทางตรงและทางอ้อม<br /><span class=\"font21\">วิธีการขอรับการสนับสนุน</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๑. โครงการที่มีการดำเนินงานในกรุงเทพฯ ให้ส่งเรื่องที่สำนักงานปลัดกระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์ เลขที่ ๑๐๓๔ ถนนกรุงเกษม แขวงมหานาค เขตป้อมปราบศัตรูพ่าย กรุงเทพมหานคร ๑๐๑๐๐ จำนวน ๒ ชุด (ภายในเดือนกันยายน &ndash; เดือนตุลาคมหรือตามที่กำหนด)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๒. โครงการที่เสนอขอรับเงินอุดหนุน มีการดำเนินงานในส่วนภูมิภาคให้องค์การส่งเรื่องที่สำนักงานพัฒนาสังคมและความมั่นคงของมนุษย์จังหวัด จำนวน ๓ ชุด เพื่อจังหวัดจะได้พิจารณากลั่นกรองในเบื้องต้นและรวบรวมพร้อมสำเนา ๒ ชุด ส่งสำนักงานปลัดกระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์ (ภายในเดือนกันยายน &ndash; เดือนตุลาคมหรือตามที่กำหนด)<br /><span class=\"font21\">ขั้นตอนการพิจารณา</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; องค์การยื่นเสนอโครงการล่วงหน้าเพื่อขอรับการสนับสนุนเงินอุดหนุนปีงบประมาณถัดไป ในช่วง เดือนกันยายน &ndash; เดือนตุลาคม ของแต่ละปี ซึ่งมีขั้นตอนการพิจารณาโดยสรุป ดังนี้<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๑. คณะทำงานพิจารณากลั่นกรองโครงการที่ขอรับเงินอุดหนุน จำนวน ๗ กลุ่มเป้าหมาย พิจารณาคัดเลือกโครงการและจัดเรียงลำดับตามความสำคัญ (เดือนมกราคม &ndash; เดือนกุมภาพันธ์)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๒. คณะกรรมการพิจารณาให้เงินอุดหนุนแก่องค์การสวัสดิการสังคมภาคเอกชนให้ความเห็นชอบโครงการที่ได้รับการพิจารณาตามข้อ 1 (เดือนกุมภาพันธ์ &ndash; เดือนมีนาคม)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๓. นำผลเสนอขอตั้งงบประมาณประจำปีต่อสำนักงบประมาณ (เดือนมีนาคม &ndash; เดือนเมษายน)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๔. สำนักงบประมาณ แจ้งอนุมัติกรอบวงเงินสนับสนุน (เดือนเมษายน &ndash; เดือนพฤษภาคม)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๕. แจ้งองค์การยืนยันการขอรับการสนับสนุน (เดือนมิถุนายน &ndash; เดือนกรกฎาคม)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๖. กระทรวงการคลังอนุมัติงวดเงินงบประมาณรายจ่ายประจำปี (เดือนตุลาคม &ndash; เดือนพฤศจิกายน)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๗. กระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์ โอนเงินให้องค์การที่ได้รับการสนับสนุน (เดือนพฤศจิกายน)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ๘. องค์การรับเงินและดำเนินงานตามแผนงานโครงการ ปีงบประมาณที่ได้รับ (เดือนตุลาคม &ndash; เดือนกันยายน)<br /><span class=\"font21\">การรายงานผลการปฏิบัติงานและการใช้จ่ายเงิน / การติดตามและประเมินผลการดำเนินงาน</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๑. การรายงานผลการปฏิบัติงานและการใช้จ่ายเงินอุดหนุน<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๑) องค์การสวัสดิการสังคมภาคเอกชนที่ได้รับเงินอุดหนุน ต้องดำเนินงานให้เป็นไปตามแผนงาน โครงการ ที่ได้ยื่นในการขอรับเงินอุดหนุน และต้องรายงานผลการปฏิบัติงานและการใช้จ่ายเงินของเงินอุดหนุน ตามแบบรายงานผลการปฏิบัติงานและการใช้จ่ายเงินอุดหนุนที่กำหนด ให้กระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์ จำนวน ๒ ชุด ตามกำหนด ดังนี้<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- กรณีเป็น โครงการระยะสั้น (ระยะเวลาดำเนินการไม่เกิน ๑๘๐ วัน) กำหนดให้องค์การสวัสดิการสังคมภาคเอกชน รายงานภายใน ๓๐ วัน นับแต่วันเสร็จสิ้นโครงการ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- กรณีเป็น โครงการระยะยาว (ดำเนินการตลอดปี) กำหนดให้องค์การสวัสดิการสังคมภาคเอกชน รายงานปีละ ๒ ครั้ง คือ<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;* ครั้งที่ ๑ ผลการปฏิบัติงานและการใช้จ่ายเงิน ตั้งแต่เดือน ตุลาคม &ndash; มีนาคม โดยให้รายงานภายใน เดือน เมษายน<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;* ครั้งที่ ๒ ผลการปฏิบัติงานและการใช้จ่ายเงิน ตั้งแต่เดือน เมษายน - กันยายน โดยให้รายงานภายใน เดือน ตุลาคม<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๒) การจัดส่งรายงานผลการปฏิบัติงานและการใช้จ่ายเงินอุดหนุน<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- องค์การสวัสดิการสังคมภาคเอกชน ในส่วนภูมิภาค ให้จัดส่งรายงานผลการปฏิบัติงานและการใช้จ่ายเงินอุดหนุน ผ่านสำนักงานพัฒนาสังคมและความมั่นคงของมนุษย์จังหวัด และส่งให้กองบริหารกองทุน สำนักงานปลัดกระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์ จำนวน ๑ ชุด<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- องค์การสวัสดิการสังคมภาคเอกชน ในเขตกรุงเทพมหานคร ให้จัดส่งรายงานผล การปฏิบัติงานและการใช้จ่ายเงินอุดหนุน ให้กองบริหารกองทุน สำนักงานปลัดกระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๓) สำนักงานปลัดกระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์ โดยกองบริหารกองทุน และสำนักงานพัฒนาสังคมและความมั่นคงของมนุษย์จังหวัด ต้องให้ข้อคิดเห็นและข้อเสนอแนะเกี่ยวกับองค์การสวัสดิการสังคมภาคเอกชน หรือ ผลการดำเนินงานตามโครงการที่ได้รับเงินอุดหนุน ในส่วนท้ายของแบบรายงานผลการปฏิบัติงานและการใช้จ่ายเงินอุดหนุน<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;๒. การติดตามผลและประเมินผลการดำเนินงาน<br />องค์การสวัสดิการสังคมภาคเอกชนจะต้องยินยอมให้เจ้าหน้าที่สำนักงานพัฒนาสังคมและความมั่นคงของมนุษย์จังหวัด และ/หรือ เจ้าหน้าที่กองบริหารกองทุน สำนักงานปลัดกระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์ เข้าติดตาม / ตรวจสอบและประเมินผลการดำเนินงาน รวมทั้งให้ความร่วมมือในการชี้แจงข้อเท็จจริง เพื่อประกอบการประเมินผลด้วย<br /><span class=\"font21\">เอกสารที่ใช้ในการเสนอโครงการ</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ให้เขียนโครงการตามแบบเสนอโครงการที่สำนักงานปลัดกระทรวงการพัฒนาสังคมและความมั่นคงของมนุษย์กำหนด ทั้งนี้ขอรับแบบฟอร์มได้ที่ กองบริหารกองทุน สำนักงานปลัดกระทรวงการพัฒนาสังคมและ ความมั่นคงของมนุษย์ เลขที่ ๓๔ อาคาร ซี.พี.ทาวเวอร์ ๓ อาคาร A ชั้น ๖ ถนนพญาไท แขวงทุ่งพญาไท เขตราชเทวี กรุงเทพฯ ๑๐๔๐๐ หรือสำนักงานพัฒนาสังคมและความมั่นคงของมนุษย์จังหวัดทุกจังหวัด หรือสามารถดาวน์โหลดแบบฟอร์มได้ที่ www.fund.m-society.go.th<br /><strong>________________________________________________________________________________________</strong></td>\n</tr>\n</tbody>\n</table>\n<p><strong><a class=\"font24\" href=\"http://www.fund.m-society.go.th/%E0%B9%84%E0%B8%9F%E0%B8%A5%E0%B9%8C%E0%B8%87%E0%B8%B2%E0%B8%99/%E0%B9%80%E0%B8%81%E0%B8%B5%E0%B9%88%E0%B8%A2%E0%B8%A7%E0%B8%81%E0%B8%B1%E0%B8%9A%20%E0%B8%81%E0%B8%9A%E0%B8%97/%E0%B8%A0%E0%B8%B2%E0%B8%A3%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B9%81%E0%B8%A5%E0%B8%B0%E0%B8%AB%E0%B8%99%E0%B9%89%E0%B8%B2%E0%B8%97%E0%B8%B5%E0%B9%88/%E0%B8%A0%E0%B8%B2%E0%B8%A3%E0%B8%81%E0%B8%B4%E0%B8%88%20%E0%B8%AB%E0%B8%99%E0%B9%89%E0%B8%B2%E0%B8%97%E0%B8%B5%E0%B9%88.docx\"><img src=\"http://www.fund.m-society.go.th/pic/foldr_16.gif\" border=\"0\" alt=\"\" width=\"16\" height=\"16\" /></a>&nbsp;&nbsp;<a class=\"font24\" href=\"http://www.fund.m-society.go.th/%E0%B9%84%E0%B8%9F%E0%B8%A5%E0%B9%8C%E0%B8%87%E0%B8%B2%E0%B8%99/%E0%B9%80%E0%B8%87%E0%B8%B4%E0%B8%99%E0%B8%AD%E0%B8%B8%E0%B8%94%E0%B8%AB%E0%B8%99%E0%B8%B8%E0%B8%99%E0%B8%AD%E0%B8%87%E0%B8%84%E0%B9%8C%E0%B8%81%E0%B8%B2%E0%B8%A3%E0%B8%AA%E0%B8%A7%E0%B8%B1%E0%B8%AA%E0%B8%94%E0%B8%B4%E0%B8%81%E0%B8%B2%E0%B8%A3%E0%B8%AA%E0%B8%B1%E0%B8%87%E0%B8%84%E0%B8%A1%E0%B8%A0%E0%B8%B2%E0%B8%84%E0%B9%80%E0%B8%AD%E0%B8%81%E0%B8%8A%E0%B8%99/%E0%B9%80%E0%B8%81%E0%B8%B5%E0%B9%88%E0%B8%A2%E0%B8%A7%E0%B8%81%E0%B8%B1%E0%B8%9A%E0%B9%80%E0%B8%87%E0%B8%B4%E0%B8%99%E0%B8%AD%E0%B8%B8%E0%B8%94%E0%B8%AB%E0%B8%99%E0%B8%B8%E0%B8%99%E0%B8%AD%E0%B8%87%E0%B8%84%E0%B9%8C%E0%B8%81%E0%B8%B2%E0%B8%A3%E0%B8%AA%E0%B8%A7%E0%B8%B1%E0%B8%AA%E0%B8%94%E0%B8%B4%E0%B8%81%E0%B8%B2%E0%B8%A3%E0%B8%AA%E0%B8%B1%E0%B8%87%E0%B8%84%E0%B8%A1%E0%B8%A0%E0%B8%B2%E0%B8%84%E0%B9%80%E0%B8%AD%E0%B8%81%E0%B8%8A%E0%B8%99.pdf\" target=\"_blank\">ดาวน์โหลดเอกสาร</a></strong></p>', null, null, '49', 'approve', '2014-12-19 13:50:06', '2014-12-19 13:50:06', null, '0');
INSERT INTO `contents` VALUES ('00000000027', 'เงินอุดหนุนองค์กรสวัสดิการสังคม', 'แบบฟอร์ม', 'แบบฟอร์ม', 'แบบฟอร์ม', '<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"620\">\n<tbody>\n<tr>\n<td class=\"font5\" align=\"center\" bgcolor=\"#DCF3FF\">แบบฟอร์มที่เกี่ยวข้องกับกองทุน</td>\n</tr>\n</tbody>\n</table>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"620\">\n<tbody>\n<tr>\n<td class=\"font1\" align=\"left\" bgcolor=\"#DCF3FF\">\n<p>1. คำขอขึ้นทะเบียนองค์การสวัสดิการสังคมภาคเอกชน&nbsp;<br />2. แบบฟอร์มการขอรับเงินอุดหนุนองค์การสวัสดิการสังคมภาคเอกชน ประจำปีงบประมาณ ....&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;-&nbsp;<span style=\"text-decoration: underline;\">ส่วนที่ 1</span>&nbsp; ข้อมูลทั่วไป&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;-&nbsp;<span style=\"text-decoration: underline;\">ส่วนที่ 2</span>&nbsp; รายละเอียดข้อมูลโครงการขอรับการสนับสนุนเงินอุดหนุน (แยกตามรายโครงการ)&nbsp;<br />3. แบบรายงานผลการปฏิบัติงานและการใช้จ่ายเงินอุดหนุน ประจำปีงบประมาณ ...&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table>', null, null, '49', 'approve', '2014-12-19 13:53:07', '2014-12-19 13:53:07', null, '0');
